import {
  Table,
  Row,
  Col,
  Container,
  Form,
  Modal,
  Button,
} from "react-bootstrap";
import { useState, useContext } from "react";
import { DataContext } from "../../Data/DataContext";
import "./Admin.css";
import Tr from "./Tr";
import { InputGroup, FormControl } from "react-bootstrap";
import $ from "jquery";
function Admin() {
  const [
    nav,
    setNav,
    admin,
    setAdmin,
    user,
    setUser,
    clinic,
    setClinic,
    ads,
    setAds,
    userModal,
    setUserModal,
    useredit,
    setUseredit,
    admins,
    setAdmins,
  ] = useContext(DataContext);

  // Thus function to update user user
  const addAdmin = () => {
    var name = $("#admin_name").val(),
      password = $("#admin_password").val(),
      role = $("#admin_role").val();

    $.ajax({
      url: "http://touch.extra-laboratory.com/api/add_admin.php",
      type: "post",
      data: {
        name: name,
        password: password,
        role: role,
      },
      success: function (data) {
        alert("success");
        if (data.code == 200) {
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_admin.php",
            success: function (data) {
              setAdmins(data);
            },
            error: function (error) {
              console.log(error);
            },
          });
        } else alert(data.message);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div className="Admin">
      <Container>
        <Row style={{ overflow: "auto", height: "90vh" }}>
          <Col md={8} xs={12}>
            <Table
              style={{ marginTop: "10px" }}
              className="table-borderless"
              striped
              bordered
              hover
              variant="dark"
            >
              <thead>
                <tr>
                  <th>#</th>
                  <th>Name</th>
                  <th>Role</th>
                  <th>Creation Date</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {admins.data.map((data) => (
                  <Tr
                    name={data.name}
                    role={data.role}
                    date={data.date}
                    delete={data.id}
                  />
                ))}
              </tbody>
            </Table>
          </Col>
          <Col md={4} xs={12}>
            <Modal.Dialog size="sm" style={{ width: "100%" }}>
              <Modal.Header>
                <Modal.Title style={{ fontSize: "large" }}>
                  Add New Admin:
                </Modal.Title>
              </Modal.Header>

              <Modal.Body>
                <InputGroup className="mb-3">
                  <InputGroup.Text id="inputGroup-sizing-default">
                    User Name
                  </InputGroup.Text>
                  <FormControl
                    id="admin_name"
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>
                <InputGroup className="mb-3">
                  <InputGroup.Text id="inputGroup-sizing-default">
                    Password
                  </InputGroup.Text>
                  <FormControl
                    id="admin_password"
                    aria-label="Default"
                    aria-describedby="inputGroup-sizing-default"
                  />
                </InputGroup>
                <Form.Select
                  id="admin_role"
                  aria-label="Default select example"
                >
                  <option value="Editor">Editor</option>
                  <option value="Admin">Admin</option>
                </Form.Select>
              </Modal.Body>

              <Modal.Footer>
                <Button
                  onClick={addAdmin}
                  style={{ width: "100%" }}
                  variant="primary"
                >
                  Add admin
                </Button>
              </Modal.Footer>
            </Modal.Dialog>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Admin;
