import {
  Table,
  Row,
  Col,
  Container,
  Form,
  Modal,
  Button,
} from "react-bootstrap";
import { useState, useContext } from "react";
import { DataContext } from "../../Data/DataContext";
import "./Admin.css";
import Tr from "./Tr";
import { InputGroup, FormControl } from "react-bootstrap";
import $ from "jquery";
function Admin() {
  const [
    nav,
    setNav,
    admin,
    setAdmin,
    user,
    setUser,
    clinic,
    setClinic,
    ads,
    setAds,
    userModal,
    setUserModal,
    useredit,
    setUseredit,
    admins,
    setAdmins,
  ] = useContext(DataContext);

  // Thus function to update user user
  const editRowDone = () => {
    var name = $("#edit_name").val(),
      phone = $("#edit_phone").val(),
      email = $("#edit_email").val(),
      password = $("#edit_password").val(),
      city = $("#edit_city").val();
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/edit_user.php",
      type: "post",
      data: {
        id: useredit.id,
        name: name,
        phone: phone,
        email: email,
        password: password,
        city: city,
      },
      success: function (data) {
        let u = JSON.parse(data);
        if (u.code == 200) {
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_users.php",
            success: function (data) {
              let d = JSON.parse(data);
              setUser(d);
            },
            error: function (error) {
              console.log(error);
            },
          });
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_clinics.php",
            success: function (data) {
              let d = JSON.parse(data);
              setClinic(d);
            },
            error: function (error) {
              console.log(error);
            },
          });
          setUserModal(false);
        } else alert(u.message);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div className="Admin">
      <Container>
        <Row style={{ overflow: "auto", height: "90vh" }}>
          <Col md={12} xs={12}>
            <div>
              <img
                src="../assets/icons/search.svg"
                style={{ position: "absolute", margin: "10px" }}
              />
              <Form.Control
                style={{ paddingLeft: " 100px" }}
                type="search"
                id="inputPassword5"
                aria-describedby="passwordHelpBlock"
              />
            </div>

            <Table
              className="table-borderless"
              striped
              bordered
              hover
              variant="dark"
            >
              <thead>
                <tr>
                  <th>#</th>
                  <th>Name</th>
                  <th>Role</th>
                  <th>Creation Date</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {admins.data.map((data) => (
                  <Tr
                    name={data.name}
                    role={data.role}
                    date={data.date}
                    delete={data.id}
                  />
                ))}
              </tbody>
            </Table>
          </Col>
        </Row>
      </Container>

      <Modal.Dialog>
        <Modal.Header closeButton>
          <Modal.Title>Add New Admin:</Modal.Title>
        </Modal.Header>

        <Modal.Footer>
          <Button variant="primary">Save changes</Button>
        </Modal.Footer>
      </Modal.Dialog>
    </div>
  );
}

export default Admin;
