import { Table, Row, Col, Container, Button } from "react-bootstrap";
import "./Ads.css";
import { useContext } from "react";
import { DataContext } from "../../Data/DataContext";
import $ from "jquery";
function Tr(props) {
  const [
    nav,
    setNav,
    admin,
    setAdmin,
    user,
    setUser,
    clinic,
    setClinic,
    ads,
    setAds,
    userModal,
    setUserModal,
    useredit,
    setUseredit,
  ] = useContext(DataContext);

  // Ths function to display the user data that want to edit
  const editRow = (id, name) => {
    setUserModal(true);
    if (name == "user") {
      const u = user.data.filter((d) => d.id == id);
      setUseredit(u[0]);
    } else if (name == "ads") {
      const u = ads.data.filter((d) => d.id == id);
      setUseredit(u[0]);
    } else {
      const u = clinic.data.filter((d) => d.id == id);
      setUseredit(u[0]);
    }
  };

  // Thus function to delete  user
  const deleteRow = (id) => {
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/delete_ads.php",
      type: "post",
      data: {
        id: id,
      },
      success: function (data) {
        let u = JSON.parse(data);
        console.log(u);
        if (u.code == 200) {
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_admin_ads.php",
            success: function (data) {
              let d = JSON.parse(data);
              setAds(d);
              console.log(d);
            },
            error: function (error) {
              console.log(error);
            },
          });
        } else alert(u.message);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <tr>
      <th>#</th>
      <th>{props.name}</th>
      <th>{props.time}</th>
      <th>
        {() => {
          if (props.img == "") {
            <a href={"http://touch.extra-laboratory.com/api/img/" + props.img}>
              download
            </a>;
          } else {
            props.img;
          }
        }}
      </th>
      <th>
        <a href={"http://touch.extra-laboratory.com/api/img/" + props.contract}>
          download
        </a>
      </th>
      <th>{props.finished}</th>
      <th>{props.date}</th>
      <th>
        <Button
          variant="outline-primary"
          size="sm"
          onClick={() => editRow(props.delete, props.type)}
        >
          Edit
        </Button>
      </th>
      <th>
        <Button
          onClick={() => deleteRow(props.delete)}
          variant="outline-primary"
          size="sm"
        >
          Delete
        </Button>
      </th>
    </tr>
  );
}

export default Tr;
