import { Container, Row, Col, Card } from "react-bootstrap";
import "./Home.css";
import StatisticHome from "./StatisticHome";
import SubHome from "./SubHome";
import { useContext } from "react";
import { DataContext } from "../../Data/DataContext";
function Home() {
  const [nav, setNav, admin, setAdmin, user, setUser, clinic, setClinic] =
    useContext(DataContext);
  return (
    <div>
      <Container className="Home" fluid>
        {/* Stack the columns on mobile by making one full-width and the other half-width */}
        <Row>
          <Col xs={12} md={4}>
            <SubHome
              title="Users"
              subtitle="Number of touch end users"
              no={user.num}
              src="./assets/icons/people-fill.svg"
            />
          </Col>
          <Col xs={12} md={4}>
            <SubHome
              title="Clincs"
              subtitle="Number of registered clinics"
              no={clinic.num}
              src="./assets/icons/hospital-fill.svg"
            />
          </Col>
          <Col xs={12} md={4}>
            <SubHome
              title="Users&Clinics"
              subtitle="Total of users"
              no={user.num + clinic.num}
              src="./assets/icons/collection-fill.svg"
            />
          </Col>
        </Row>
      </Container>

      <Container className="Home" style={{ marginTop: "20px" }} fluid>
        {/* Stack the columns on mobile by making one full-width and the other half-width */}
        <Row>
          <Col xs={12} md={12}>
            <StatisticHome
              user={user.num}
              clinic={clinic.num}
              total={clinic.num + user.num}
              android={clinic.num + user.num}
              ios={0}
            />
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default Home;
