import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "../../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import $ from "jquery";
import "../../App.css";

function Login() {
  const [
    user,
    setUser,
    experience,
    setExperience,
    education,
    setEducation,
    skill,
    setSkill,
    token,
    setToken,
    useredit,
    setUseredit,
  ] = useContext(DataContext);
  const navigate = useNavigate();

  // This signup function to add user data to database
  const signup = () => {
    var name = $("#signup-name").val(),
      email = $("#signup-email").val(),
      phone = $("#signup-phone").val(),
      password = $("#signup-password").val();
    $.ajax({
      url: "http://localhost:8888/project/public/api/signup",
      type: "post",
      data: {
        name: name,
        email: email,
        phone: phone,
        password: password,
        cv: "cv",
      },
      beforeSend: function () {
        $("#loader").show();
      },
      headers: {
        Accept: "application/json",
      },
      success: function (data) {
        $("#loader").hide();
        alert(data.message);
        $("#signup-name").val("");
        $("#signup-email").val("");
        $("#signup-phone").val("");
        $("#signup-password").val("");
      },
      error: function (error) {
        $("#loader").hide();
        console.log(error);
        alert("something wronge.");
      },
    });
  };

  const login = () => {
    var email = $("#login-email").val(),
      password = $("#login-password").val();
    $.ajax({
      url: "http://localhost:8888/project/public/api/login",
      type: "post",
      data: {
        email: email,
        password: password,
      },
      beforeSend: function () {
        $("#loader").show();
      },
      headers: {
        Accept: "application/json",
      },
      success: function (data) {
        $("#loader").hide();
        if (data.code == 200) {
          setToken(data.token);
          console.log(token);
          setUser(data.user);
          getExperiences();
          getEducation();
          getSkill();
          navigate("./home", { replace: true });
          $("#login-email").val("");
          $("#login-password").val("");
        } else alert(data.message);

        // console.log(token);
      },
      error: function (error) {
        $("#loader").hide();
        console.log(error);
        alert("something wronge.");
      },
    });
  };

  // This function to fetch getExperiences data from database
  const getExperiences = () => {
    $.ajax({
      url: "http://localhost:8888/project/public/api/get_experience",
      type: "post",
      success: function (data) {
        setExperience(data.data);
        console.log(setExperience);
      },
      headers: {
        Accept: "application/json",
        Authorization: "Bearer " + token,
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This function to fetch getExperiences data from database
  const getEducation = () => {
    $.ajax({
      url: "http://localhost:8888/project/public/api/get_education",
      type: "post",
      headers: {
        Accept: "application/json",
        Authorization: "Bearer " + token,
      },
      success: function (data) {
        setEducation(data.data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This function to fetch getExperiences data from database
  const getSkill = () => {
    $.ajax({
      url: "http://localhost:8888/project/public/api/get_skill",
      type: "post",
      headers: {
        Accept: "application/json",
        Authorization: "Bearer " + token,
      },
      success: function (data) {
        setSkill(data.data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div style={{ backgroundColor: "gray", height: "100vh" }}>
      <br />
      <br />

      <Container id="loginout">
        <Row>
          <Col xs={12} md={5} style={{ float: "right" }}>
            <Card
              id="login"
              style={{
                zIndex: "9999",
                width: "100%",
                height: "",
