import {
  Table,
  Row,
  Col,
  Container,
  Form,
  Modal,
  Button,
} from "react-bootstrap";
import { useState, useContext } from "react";
import { DataContext } from "../../Data/DataContext";
import "./Covid.css";
import Tr from "../Clinic/Tr";
import { InputGroup, FormControl } from "react-bootstrap";
import $ from "jquery";

function Covid() {
  const [
    nav,
    setNav,
    admin,
    setAdmin,
    user,
    setUser,
    clinic,
    setClinic,
    ads,
    setAds,
    userModal,
    setUserModal,
    useredit,
    setUseredit,
  ] = useContext(DataContext);

  // Thus function to update user user
  const editRowDone = () => {
    var name = $("#edit_name").val(),
      phone = $("#edit_phone").val(),
      email = $("#edit_email").val(),
      password = $("#edit_password").val(),
      city = $("#edit_city").val();
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/edit_user.php",
      type: "post",
      data: {
        id: useredit.id,
        name: name,
        phone: phone,
        email: email,
        password: password,
        city: city,
      },
      success: function (data) {
        if (data.code == 200) {
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_users.php",
            success: function (data) {
              setUser(data);
            },
            error: function (error) {
              console.log(error);
            },
          });
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_clinics.php",
            success: function (data) {
              setClinic(data);
            },
            error: function (error) {
              console.log(error);
            },
          });
          setUserModal(false);
        } else alert(data.message);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div>
      <br />
      <br />
      <br />
      <br />
      <br />
      <Container>
        <Row style={{ overflow: "auto", height: "90vh", width: "100%" }}>
          <Col md={12} xs={12}>
            {/* <div>
              <img
                src="../assets/icons/search.svg"
                style={{ position: "absolute", margin: "10px" }}
              />
              <Form.Control
                style={{ paddingLeft: " 100px" }}
                type="search"
                id="inputPassword5"
                aria-describedby="passwordHelpBlock"
              />
            </div> */}
            <Button
              style={{
                backgroundColor: "black",
                marginBottom: "10px",
                position: "absolute",
                top: "0px",
              }}
            >
              Add new +
            </Button>
            <Table
              className="table-borderless"
              striped
              bordered
              hover
              variant="dark"
            >
              <thead>
                <tr>
                  <th>#</th>
                  <th>Full Name</th>
                  <th>Phone</th>
                  <th>Email</th>
                  <th>City</th>
                  <th>Account</th>
                  <th>Date</th>
                  <th>Edit</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {user.data.map((data) => (
                  <Tr
                    name={data.name}
                    phone={data.phone}
                    email={data.email}
                    city={data.city}
                    account={data.app}
                    date={data.date}
                    dalet={data.id}
                    delete={data.id}
                    type="user"
                  />
                ))}
              </tbody>
            </Table>
          </Col>
        </Row>
      </Container>
      <Modal
        show={userModal}
        onHide={() => setUserModal(false)}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Manageing Users
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Full Name
            </InputGroup.Text>
            <FormControl
              id="edit_name"
              defaultValue={useredit.name}
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
            />
          </InputGroup>
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Phone
            </InputGroup.Text>
            <FormControl
              id="edit_phone"
              defaultValue={useredit.phone}
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
            />
          </InputGroup>
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Email
            </InputGroup.Text>
            <FormControl
              id="edit_email"
              defaultValue={useredit.email}
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
            />
          </InputGroup>
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Password
            </InputGroup.Text>
            <FormControl
              id="edit_password"
              defaultValue={useredit.password}
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
            />
          </InputGroup>
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              City
            </InputGroup.Text>
            <FormControl
              id="edit_city"
              defaultValue={useredit.city}
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
            />
          </InputGroup>
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Account type
            </InputGroup.Text>
            <FormControl
              id="edit_app"
              defaultValue={useredit.app}
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
            />
          </InputGroup>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={() => editRowDone()}>Make edit</Button>
          <Button onClick={() => setUserModal(false)}>Close</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Covid;
