import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "./../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";

function Login() {
  const [nav, setNav, admin, setAdmin, user, setUser, clinic, setClinic] =
    useContext(DataContext);
  const navigate = useNavigate();

  // This login function that deal with api
  const loginAdmin = () => {
    let name = document.getElementById("login-name").value;
    let password = document.getElementById("login-pass").value;
    fetch("http://touch.extra-laboratory.com/api/admin_login.php", {
      method: "POST",
      body:
        "name=" +
        encodeURIComponent(name) +
        "&password=" +
        encodeURIComponent(password) +
        "&userID=3",
      headers: {
        "Content-type": "application/x-www-form-urlencoded",
      },
    })
      .then(function (response) {
        if (response.ok) {
          return response.json();
        }
        return Promise.reject(response);
      })
      .then(function (data) {
        if (data.code == 200) {
          getUser();
          getClinic();
          setAdmin(data.admin);
          navigate("./home", { replace: true });
        } else {
          alert(data.message);
        }
      })
      .catch(function (error) {
        console.warn("Something went wrong.", error);
      });
  };

  // This login function to fetch users data from database
  const getUser = () => {
    fetch("http://touch.extra-laboratory.com/api/get_users.php", {
      method: "POST",
      headers: {
        "Content-type": "application/x-www-form-urlencoded",
      },
    })
      .then(function (response) {
        if (response.ok) {
          return response.json();
        }
        return Promise.reject(response);
      })
      .then(function (data) {
        setUser(data);
        console.log(data);
      })
      .catch(function (error) {
        console.warn("Something went wrong.", error);
      });
  };

  // This login function to fetch users data from database
  const getClinic = () => {
    fetch("http://touch.extra-laboratory.com/api/get_clinics.php", {
      method: "POST",
      headers: {
        "Content-type": "application/x-www-form-urlencoded",
      },
    })
      .then(function (response) {
        if (response.ok) {
          return response.json();
        }
        return Promise.reject(response);
      })
      .then(function (data) {
        setClinic(data);
        console.log(data);
      })
      .catch(function (error) {
        console.warn("Something went wrong.", error);
      });
  };

  return (
    <div className="Login" style={{ backgroundColor: "gray", height: "100vh" }}>
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />

      <Container>
        <Row>
          <center>
            <Col xs={12} md={5}>
              <Card
                style={{
                  width: "100%",
                  height: "",
                  backgroundColor: "rgb(77, 77, 77)",
                  color: "rgb(206, 206, 206)",
                  boxShadow: "white 0px 0px 5px 0px",
                  borderRadius: "10px",
                }}
              >
                <Card.Header
                  style={{
                    background: "rgb(31, 31, 31)",
                    borderRadius: "10px 10px 0px 0px",
                  }}
                >
                  <strong>Login board</strong>
                </Card.Header>
                <Card.Body>
                  <Container fluid>
                    <Row style={{ margin: "10px" }}>
                      <Col xs={12} md={4}>
                        <strong style={{ float: "left", fontSize: "small" }}>
                          <label>User Name:</label>
                        </strong>
                      </Col>
                      <Col xs={12} md={8}>
                        <input
                          id="login-name"
                          style={{
                            float: "left",
                            border: "none",
                            borderBottom: "blue solid 1px",
                            backgroundColor: "rgb(206, 206, 206)",
                            width: "100%",
                          }}
                          type="text"
                        />
                      </Col>
                    </Row>

                    <Row style={{ margin: "10px" }}>
                      <Col xs={12} md={4}>
                        <strong style={{ float: "left", fontSize: "small" }}>
                          <label>Password:</label>
                        </strong>
                      </Col>
                      <Col xs={12} md={8}>
                        <input
                          id="login-pass"
                          style={{
                            float: "left",
                            border: "none",
                            borderBottom: "blue solid 1px",
                            backgroundColor: "rgb(206, 206, 206)",
                            width: "100%",
                          }}
                          type="password"
                        />
                      </Col>
                    </Row>

                    <Row style={{ margin: "10px" }}>
                      <Col md={4}></Col>
                      <Col xs={12} md={8}>
                        <Button
                          onClick={loginAdmin}
                          style={{ float: "left", width: "100%" }}
                          variant="primary"
                        >
                          Go
                        </Button>
                      </Col>
                    </Row>
                  </Container>
                </Card.Body>
              </Card>
            </Col>
          </center>
        </Row>
      </Container>
    </div>
  );
}

export default Login;
