import {
  Table,
  Row,
  Col,
  Container,
  Form,
  Modal,
  Button,
} from "react-bootstrap";
import { useState, useContext } from "react";
import { DataContext } from "../../Data/DataContext";
import "./Ads.css";
import Tr from "./Tr";
import { InputGroup, FormControl } from "react-bootstrap";
import $ from "jquery";

function Ads() {
  const [
    nav,
    setNav,
    admin,
    setAdmin,
    user,
    setUser,
    clinic,
    setClinic,
    ads,
    setAds,
    userModal,
    setUserModal,
    useredit,
    setUseredit,
  ] = useContext(DataContext);

  // Thus function to change status of the ads
  const changeStatus = (id, i) => {
    if (i == 0) {
      i = 1;
    } else {
      i = 0;
    }
    useredit.status = i;
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/status.php",
      type: "post",
      data: {
        id: id,
        i: i,
      },
      success: function (data) {
        $.ajax({
          url: "http://touch.extra-laboratory.com/api/get_admin_ads.php",
          success: function (data) {
            setAds(data);
          },
          error: function (error) {
            console.log(error);
          },
        });
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // Thus function to change status of the ads
  const statusActive = (id) => {
    var time = $("#edit_time").val();
    alert(id);
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/test.php",
      type: "post",
      data: {
        id: id,
        time: time,
      },
      success: function (data) {
        console.log(data);
        $.ajax({
          url: "http://touch.extra-laboratory.com/api/get_admin_ads.php",
          success: function (data) {
            setAds(data);
          },
          error: function (error) {
            console.log(error);
          },
        });
      },
      error: function (error) {},
    });
  };

  // Thus function to update user user
  const editRowDone = () => {
    var name = $("#edit_name").val(),
      phone = $("#edit_phone").val(),
      email = $("#edit_email").val(),
      password = $("#edit_password").val(),
      city = $("#edit_city").val();
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/edit_user.php",
      type: "post",
      data: {
        id: useredit.id,
        name: name,
        phone: phone,
        email: email,
        password: password,
        city: city,
      },
      success: function (data) {
        if (data.code == 200) {
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_users.php",
            success: function (data) {
              setUser(data);
            },
            error: function (error) {
              console.log(error);
            },
          });
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_clinics.php",
            success: function (data) {
              setClinic(data);
            },
            error: function (error) {
              console.log(error);
            },
          });
          setUserModal(false);
        } else alert(data.message);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div className="Ads">
      <Container>
        <Row style={{ overflow: "auto", height: "90vh" }}>
          <Col md={12} xs={12}>
            {/* <div>
              <img
                src="../assets/icons/search.svg"
                style={{ position: "absolute", margin: "10px" }}
              />
              <Form.Control
                style={{ paddingLeft: " 100px" }}
                type="search"
                id="inputPassword5"
                aria-describedby="passwordHelpBlock"
              />
            </div> */}

            <Table
              className="table-borderless"
              striped
              bordered
              hover
              variant="dark"
            >
              <thead>
                <tr>
                  <th>#</th>
                  <th>Owner</th>
                  <th>Ads Long</th>
                  <th>Ads Image</th>
                  <th>Signed Contract</th>
                  <th>Finished on</th>
                  <th>Date</th>
                  <th>Edit</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {ads.data.map((data) => (
                  <Tr
                    name={data.owner}
                    time={data.time}
                    img={data.img}
                    contract={data.contract}
                    finished={data.finished}
                    date={data.date}
                    delete={data.id}
                    type="ads"
                  />
                ))}
              </tbody>
            </Table>
          </Col>
        </Row>
      </Container>
      <Modal
        show={userModal}
        onHide={() => setUserModal(false)}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Manageing Ads
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Check
            style={{ margin: "10px" }}
            type="switch"
            id="custom-switch"
            label={"The show status of the Ads"}
            defaultChecked={parseInt(useredit.status)}
            onClick={() => changeStatus(useredit.id, useredit.status)}
          />
          <InputGroup className="mb-3">
            <InputGroup.Text id="inputGroup-sizing-default">
              Remaining Days
            </InputGroup.Text>
            <FormControl
              id="edit_time"
              defaultValue={useredit.time}
              aria-label="Default"
              aria-describedby="inputGroup-sizing-default"
            />
            <Button
              variant="outline-secondary"
              id="button-addon2"
              onClick={() => statusActive(useredit.id)}
            >
              Activate The Ads
            </Button>
          </InputGroup>
          <textarea disabled style={{ width: "100%" }}>
            {useredit.body}
          </textarea>
          <img
            src={
              "http://touch.extra-laboratory.com/api/img/" + useredit.contract
            }
            style={{ width: "100%", borderRadius: "5px" }}
          />
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default Ads;
