import { Table, Row, Col, Container, Button } from "react-bootstrap";
import Clinic from "./Clinic";
import "./Clinic.css";
import { useContext } from "react";
import { DataContext } from "../../Data/DataContext";
function Tr(props) {
  const [
    nav,
    setNav,
    admin,
    setAdmin,
    user,
    setUser,
    clinc,
    setClinic,
    ads,
    setAds,
    userModal,
    setUserModal,
    useredit,
    setUseredit,
  ] = useContext(DataContext);

  // This function to delete data of specific user
  const deleteRow = (id) => {
    alert(id);
  };

  // Ths function to display the user data that want to edit
  const editRow = (id) => {
    setUserModal(true);
    const u = clinc.data.filter((d) => d.id == id);
    setUseredit(u[0]);
  };

  return (
    <tr>
      <th>#</th>
      <th>{props.name}</th>
      <th>{props.phone}</th>
      <th>{props.email}</th>
      <th>{props.city}</th>
      <th>{props.account}</th>
      <th>{props.date}</th>
      <th>
        <Button
          variant="outline-primary"
          size="sm"
          onClick={() => editRow(props.delete)}
        >
          Edit
        </Button>
      </th>
      <th>
        <Button
          onClick={() => deleteRow(props.delete)}
          variant="outline-primary"
          size="sm"
        >
          Delete
        </Button>
      </th>
    </tr>
  );
}

export default Tr;
