import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "../../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import $ from "jquery";
import "../../App.css";

function Login() {
  const [
    admin,
    setAdmin,
    nurse,
    setNurse,
    accountant,
    setAccountant,
    audit,
    setAudit,
    report,
    setReport,
    admins,
    setAdmins,
    useredit,
    setUseredit,
    userModal,
    setUserModal,
    covid,
    setCovid,
  ] = useContext(DataContext);
  const navigate = useNavigate();

  // This login function that deal with api
  const loginAdmin = () => {
    let email = document.getElementById("login-name").value;
    let password = document.getElementById("login-pass").value;
    fetch("http://gentle.extra-laboratory.com/api/admin_login.php", {
      method: "POST",
      body:
        "email=" +
        encodeURIComponent(email) +
        "&password=" +
        encodeURIComponent(password) +
        "&userID=3",
      headers: {
        "Content-type": "application/x-www-form-urlencoded",
      },
    })
      .then(function (response) {
        if (response.ok) {
          return response.json();
        }
        return Promise.reject(response);
      })
      .then(function (data) {
        if (data.code == 200) {
          console.log(data);
          getAdmins();
          getNurse();
          getAccountant();
          getAudit();
          getCovid();
          /* getUser();
          getClinic();
          getAdmin();
          getAdminAds();
          */
          setAdmin(data.admin);
          console.log(data.admin);
          navigate("./home", { replace: true });
          $("#loader").hide();
        } else {
          alert(data.message);
        }
      })
      .catch(function (error) {
        console.warn("Something went wrong.", error);
      });
  };

  // This login function to fetch Nurse data from database
  const getNurse = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_nurse.php",
      success: function (data) {
        setNurse(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Accountant data from database
  const getAccountant = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_accountant.php",
      beforeSend: function () {},
      success: function (data) {
        setAccountant(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Audit data from database
  const getAudit = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_audit.php",
      success: function (data) {
        setAudit(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Report data from database
  const getReport = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_report.php",
      success: function (data) {
        setReport(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch admins data from database
  const getAdmins = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_admins.php",
      success: function (data) {
        setAdmins(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Covid data from database
  const getCovid = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_covid.php",
      success: function (data) {
        setCovid(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div style={{ backgroundColor: "gray", height: "100vh" }}>
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />

      <Container id="loginout">
        <Row>
          <center>
            <Col xs={12} md={5}>
              <Card
                id="login"
                style={{
                  zIndex: "9999",
                  width: "100%",
                  height: "",
                  backgroundColor: "#4346aa",
                  color: "rgb(206, 206, 206)",
                  boxShadow: "white 0px 0px 5px 0px",
                  borderRadius: "10px",
                }}
              >
                <Card.Header
                  style={{
                    background: "#2c2f8b",
                    borderRadius: "10px 10px 0px 0px",
                  }}
                >
                  <strong>Login</strong>
                </Card.Header>
                <Card.Body>
                  <Container fluid>
                    <Row style={{ margin: "10px" }}>
                      <Col xs={12} md={4}>
                        <strong style={{ float: "left", fontSize: "small" }}>
                          <label>Email:</label>
                        </strong>
                      </Col>
                      <Col xs={12} md={8}>
                        <input
                          id="login-name"
                          style={{
                            float: "left",
                            border: "none",
                            borderBottom: "blue solid 1px",
                            backgroundColor: "rgb(206, 206, 206)",
                            width: "100%",
                          }}
                          type="text"
                        />
                      </Col>
                    </Row>

                    <Row style={{ margin: "10px" }}>
                      <Col xs={12} md={4}>
                        <strong style={{ float: "left", fontSize: "small" }}>
                          <label>Password:</label>
                        </strong>
                      </Col>
                      <Col xs={12} md={8}>
                        <input
                          id="login-pass"
                          style={{
                            float: "left",
                            border: "none",
                            borderBottom: "blue solid 1px",
                            backgroundColor: "rgb(206, 206, 206)",
                            width: "100%",
                          }}
                          type="password"
                        />
                      </Col>
                    </Row>

                    <Row style={{ margin: "10px" }}>
                      <Col md={4}></Col>
                      <Col xs={12} md={8}>
                        <Button
                          onClick={loginAdmin}
                          style={{
                            float: "left",
                            width: "100%",
                            backgroundColor: "#2c2f8b",
                          }}
                          variant="primary"
                        >
                          Go
                        </Button>
                      </Col>
                    </Row>
