import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "../../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import $ from "jquery";
import "../../App.css";

function Login() {
  const [
    user,
    setUser,
    experience,
    setExperience,
    education,
    setEducation,
    skill,
    setSkill,
    token,
    setToken,
    useredit,
    setUseredit,
  ] = useContext(DataContext);
  const navigate = useNavigate();

  // This signup function to add user data to database
  const signup = () => {
    var name = $("#signup-name").val(),
      email = $("#signup-email").val(),
      phone = $("#signup-phone").val(),
      password = $("#signup-password").val();
    $.ajax({
      url: "http://localhost:8888/project/public/api/signup",
      type: "post",
      data: {
        name: name,
        email: email,
        phone: phone,
        password: password,
        cv: "cv",
      },
      beforeSend: function () {
        $("#loader").show();
      },
      headers: {
        Accept: "application/json",
      },
      success: function (data) {
        $("#loader").hide();
        alert(data.message);
        $("#signup-name").val("");
        $("#signup-email").val("");
        $("#signup-phone").val("");
        $("#signup-password").val("");
      },
      error: function (error) {
        $("#loader").hide();
        console.log(error);
        alert("something wronge.");
      },
    });
  };

  const login = () => {
    var email = $("#login-email").val(),
      password = $("#login-password").val();
    $.ajax({
      url: "http://localhost:8888/project/public/api/login",
      type: "post",
      data: {
        email: email,
        password: password,
      },
      beforeSend: function () {
        $("#loader").show();
      },
      headers: {
        Accept: "application/json",
      },
      success: function (data) {
        setToken(data.token);
        setUser(data.user);
        $("#loader").hide();
        $("#login-email").val("");
        $("#login-password").val("");
        //console.log(data.token);
        console.log(data.user);
      },
      error: function (error) {
        $("#loader").hide();
        console.log(error);
        alert("something wronge.");
      },
    });
  };

  /* // This login function to fetch Accountant data from database
  const getAccountant = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_accountant.php",
      beforeSend: function () {},
      success: function (data) {
        setAccountant(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Audit data from database
  const getAudit = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_audit.php",
      success: function (data) {
        setAudit(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Report data from database
  const getReport = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_report.php",
      success: function (data) {
        setReport(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch admins data from database
  const getAdmins = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_admins.php",
      success: function (data) {
        setAdmins(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Covid data from database
  const getCovid = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_covid.php",
      success: function (data) {
        setCovid(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  }; */

