import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "../../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import $ from "jquery";
import "../../App.css";

function Login() {
  const [
    admin,
    setAdmin,
    nurse,
    setNurse,
    accountant,
    setAccountant,
    audit,
    setAudit,
    report,
    setReport,
    admins,
    setAdmins,
    useredit,
    setUseredit,
    userModal,
    setUserModal,
    covid,
    setCovid,
  ] = useContext(DataContext);
  const navigate = useNavigate();

  // This login function that deal with api
  const loginAdmin = () => {
    let email = document.getElementById("login-name").value;
    let password = document.getElementById("login-pass").value;
    fetch("http://gentle.extra-laboratory.com/api/admin_login.php", {
      method: "POST",
      body:
        "email=" +
        encodeURIComponent(email) +
        "&password=" +
        encodeURIComponent(password) +
        "&userID=3",
      headers: {
        "Content-type": "application/x-www-form-urlencoded",
      },
    })
      .then(function (response) {
        if (response.ok) {
          return response.json();
        }
        return Promise.reject(response);
      })
      .then(function (data) {
        if (data.code == 200) {
          console.log(data);
          getAdmins();
          getNurse();
          getAccountant();
          getAudit();
          getCovid();
          /* getUser();
          getClinic();
          getAdmin();
          getAdminAds();
          */
          setAdmin(data.admin);
          console.log(data.admin);
          navigate("./home", { replace: true });
          $("#loader").hide();
        } else {
          alert(data.message);
        }
      })
      .catch(function (error) {
        console.warn("Something went wrong.", error);
      });
  };

  // This signup function to add user data to database
  const signup = () => {
    var name = $("#signup_name").val(),
      email = $("#signup-email").val(),
      phone = $("#signup-phone").val(),
      password = $("#signup-password").val();
    $.ajax({
      url: "http://localhost:8888/project/public/api/signup",
      success: function (data) {
        setNurse(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Accountant data from database
  const getAccountant = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_accountant.php",
      beforeSend: function () {},
      success: function (data) {
        setAccountant(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Audit data from database
  const getAudit = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_audit.php",
      success: function (data) {
        setAudit(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Report data from database
  const getReport = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_report.php",
      success: function (data) {
        setReport(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch admins data from database
  const getAdmins = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_admins.php",
      success: function (data) {
        setAdmins(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Covid data from database
  const getCovid = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_covid.php",
      success: function (data) {
        setCovid(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div style={{ backgroundColor: "gray", height: "100vh" }}>
      <br />
      <br />

