import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "../../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import $ from "jquery";
import "../../App.css";

function Login() {
  const navigate = useNavigate();
  const [
    user,
    setUser,
    experience,
    setExperience,
    education,
    setEducation,
    skill,
    setSkill,
    token,
    setToken,
  ] = useContext(DataContext);

  // This login function that deal with api
  const loginAdmin = () => {
    let email = document.getElementById("login-name").value;
    let password = document.getElementById("login-pass").value;
    fetch("http://gentle.extra-laboratory.com/api/admin_login.php", {
      method: "POST",
      body:
        "email=" +
        encodeURIComponent(email) +
        "&password=" +
        encodeURIComponent(password) +
        "&userID=3",
      headers: {
        "Content-type": "application/x-www-form-urlencoded",
      },
    })
      .then(function (response) {
        if (response.ok) {
          return response.json();
        }
        return Promise.reject(response);
      })
      .then(function (data) {
        if (data.code == 200) {
          console.log(data);
          getAdmins();
          getNurse();
          getAccountant();
          getAudit();
          getCovid();
          /* getUser();
          getClinic();
          getAdmin();
          getAdminAds();
          */
          setAdmin(data.admin);
          console.log(data.admin);
          navigate("./home", { replace: true });
          $("#loader").hide();
        } else {
          alert(data.message);
        }
      })
      .catch(function (error) {
        console.warn("Something went wrong.", error);
      });
  };

  const loginUser = () => {
    setToken("any");
    var email = $("#login-email").val(),
      password = $("#login-password").val();
    $.ajax({
      url: "http://localhost:8888/project/public/api/login",
      type: "post",
      data: {
        email: email,
        password: password,
      },
      beforeSend: function () {
        $("#loader").show();
      },
      headers: {
        Accept: "application/json",
      },
      success: function (data) {
        if (data.code == 200) {
          setTimeout(function () {
            alert(token);
          }, 1000);
          alert(token);
          console.log(user);
          setUser(data.user);
          getExperiences();
          getEducation();
          getSkill();
          navigate("./home", { replace: true });
          $("#login-email").val("");
          $("#login-password").val("");
        } else alert(data.message);

        //console.log(token);
      },
      error: function (error) {
        $("#loader").hide();
        console.log(error);
        alert("something wronge.");
      },
    });
  };

  // This signup function to add user data to database
  const signup = () => {
    var name = $("#signup-name").val(),
      email = $("#signup-email").val(),
      phone = $("#signup-phone").val(),
      password = $("#signup-password").val();
    $.ajax({
