import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "../../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import $ from "jquery";
import "../../App.css";

function Login() {
  const [
    admin,
    setAdmin,
    nurse,
    setNurse,
    accountant,
    setAccountant,
    audit,
    setAudit,
    report,
    setReport,
    admins,
    setAdmins,
    useredit,
    setUseredit,
    userModal,
    setUserModal,
    covid,
    setCovid,
  ] = useContext(DataContext);
  const navigate = useNavigate();

  // This signup function to add user data to database
  const signup = () => {
    var name = $("#signup_name").val(),
      email = $("#signup-email").val(),
      phone = $("#signup-phone").val(),
      password = $("#signup-password").val();
    $.ajax({
      url: "http://localhost:8888/project/public/api/signup",
      type: "post",
      data: { name: name, email: email, phone: phone, password: password },
      success: function (data) {
        console.log(data);
        //setNurse(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Accountant data from database
  const getAccountant = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_accountant.php",
      beforeSend: function () {},
      success: function (data) {
        setAccountant(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Audit data from database
  const getAudit = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_audit.php",
      success: function (data) {
        setAudit(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Report data from database
  const getReport = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_report.php",
      success: function (data) {
        setReport(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch admins data from database
  const getAdmins = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_admins.php",
      success: function (data) {
        setAdmins(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch Covid data from database
  const getCovid = () => {
    $.ajax({
      url: "http://gentle.extra-laboratory.com/api/get_covid.php",
      success: function (data) {
        setCovid(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <div style={{ backgroundColor: "gray", height: "100vh" }}>
      <br />
      <br />

      <Container id="loginout">
        <Row>
          <Col xs={12} md={5} style={{ float: "right" }}>
            <Card
              id="login"
              style={{
                zIndex: "9999",
                width: "100%",
                height: "",
                backgroundColor: "#4346aa",
                color: "rgb(206, 206, 206)",
                boxShadow: "white 0px 0px 5px 0px",
                borderRadius: "10px",
              }}
            >
              <Card.Header
                style={{
                  background: "#2c2f8b",
                  borderRadius: "10px 10px 0px 0px",
                }}
              >
                <strong>Login</strong>
              </Card.Header>
              <Card.Body>
                <Container fluid>
                  <Row style={{ margin: "10px" }}>
                    <Col xs={12} md={4}>
                      <strong style={{ float: "left", fontSize: "small" }}>
                        <label>Email:</label>
                      </strong>
                    </Col>
                    <Col xs={12} md={8}>
                      <input
                        id="login-email"
                        style={{
                          float: "left",
                          border: "none",
                          borderBottom: "blue solid 1px",
                          backgroundColor: "rgb(206, 206, 206)",
                          width: "100%",
                        }}
                        type="text"
                      />
                    </Col>
                  </Row>

                  <Row style={{ margin: "10px" }}>
                    <Col xs={12} md={4}>
                      <strong style={{ float: "left", fontSize: "small" }}>
                        <label>Password:</label>
                      </strong>
                    </Col>
                    <Col xs={12} md={8}>
                      <input
                        id="login-password"
                        style={{
                          float: "left",
                          border: "none",
                          borderBottom: "blue solid 1px",
                          backgroundColor: "rgb(206, 206, 206)",
                          width: "100%",
                        }}
                        type="password"
                      />
                    </Col>
                  </Row>

                  <Row style={{ margin: "10px" }}>
                    <Col md={4}></Col>
                    <Col xs={12} md={8}>
                      <Button
                        onClick={login}
                        style={{
                          float: "left",
                          width: "100%",
                          backgroundColor: "#2c2f8b",
                        }}
                        variant="primary"
                      >
                        Go
                      </Button>
                    </Col>
                  </Row>
                </Container>
              </Card.Body>
