import { Card, ListGroup } from "react-bootstrap";
import SubSidebar from "./SubSidebar";
import User from "./User";
import { Link, NavLink } from "react-router-dom";
import "./Sidebar.css";
import { useContext, useState } from "react";
import { DataContext } from "../../Data/DataContext";
function Sidebar() {
  const [nav, setNav, admin, setAdmin] = useContext(DataContext);
  alert(admin.name);
  return (
    <Card
      style={{
        backgroundColor: "rgb(37, 37, 37)",
      }}
      className="Sidebar"
    >
      <Card.Header>
        <User src="./assets/img/user.png" name={"admin.name"} />
      </Card.Header>

      <NavLink
        onClick={() => setNav("Home")}
        to="/home"
        activeStyle={{
          fontWeight: "bold",
          color: "red",
        }}
        exact={true}
      >
        <SubSidebar src="./assets/icons/house-door.svg" name="Home" />
      </NavLink>
      <NavLink
        onClick={() => setNav("Advertisement")}
        to="./ads"
        activeClassName="active-home"
      >
        <SubSidebar src="./assets/icons/badge-ad.svg" name="Advertisement" />
      </NavLink>
      <NavLink
        onClick={() => setNav("Manage Clinics")}
        to="./clinic"
        activeClassName="active-home"
      >
        <SubSidebar src="./assets/icons/hospital.svg" name="Manage clinic" />
      </NavLink>
      <NavLink
        onClick={() => setNav("Manage Users")}
        to="./user"
        activeClassName="active-home"
      >
        <SubSidebar src="./assets/icons/people.svg" name="App users" />
      </NavLink>
      <Link onClick={() => setNav("Manage Admins")} to="./admin">
        <SubSidebar
          src="./assets/icons/credit-card.svg"
          name="Admin Management"
        />
      </Link>

      <Card.Footer
        style={{ position: "absolute", bottom: "0px", width: "100%" }}
      >
        <SubSidebar src="./assets/icons/box-arrow-left.svg" name="Logout" />
      </Card.Footer>
    </Card>
  );
}

export default Sidebar;
