import { Card, Button, Container, Row, Col } from "react-bootstrap";
import { NavLink } from "react-router-dom";
import { DataContext } from "./../Data/DataContext";
import { useContext } from "react";
import { useNavigate } from "react-router-dom";
import $ from "jquery";
import "../App.css";

function Login() {
  const [
    nav,
    setNav,
    admin,
    setAdmin,
    user,
    setUser,
    clinic,
    setClinic,
    ads,
    setAds,
    userModal,
    setUserModal,
    useredit,
    setUseredit,
    admins,
    setAdmins,
  ] = useContext(DataContext);
  const navigate = useNavigate();

  // This login function that deal with api
  const loginAdmin = () => {
    let name = document.getElementById("login-name").value;
    let password = document.getElementById("login-pass").value;
    fetch("http://touch.extra-laboratory.com/api/admin_login.php", {
      method: "POST",
      body:
        "name=" +
        encodeURIComponent(name) +
        "&password=" +
        encodeURIComponent(password) +
        "&userID=3",
      headers: {
        "Content-type": "application/x-www-form-urlencoded",
      },
    })
      .then(function (response) {
        if (response.ok) {
          return response.json();
        }
        return Promise.reject(response);
      })
      .then(function (data) {
        if (data.code == 200) {
          console.log(data);
          getUser();
          getClinic();
          getAdmin();
          getAdminAds();
          setAdmin(data.admin);
          navigate("./home", { replace: true });
        } else {
          alert(data.message);
        }
      })
      .catch(function (error) {
        console.warn("Something went wrong.", error);
      });
  };

  // This login function to fetch users data from database
  const getUser = () => {
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/get_users.php",
      success: function (data) {
        setUser(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch clinics data from database
  const getClinic = () => {
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/get_clinics.php",
      success: function (data) {
        setClinic(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch admins data from database
  const getAdmin = () => {
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/get_admin.php",
      success: function (data) {
        setAdmins(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  // This login function to fetch ads to admin dashboard
  const getAdminAds = () => {
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/get_admin_ads.php",
      success: function (data) {
        setAds(data);
        console.log(data);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  $(document).ready(function () {
    setTimeout(function () {
      alert("hoi");
      $(".Login").animate(
        { backgroundImage: "url(./assets/img/user.png)" },
        30
      );
    }, 3000);
    $("#loginout").click(function () {
      $("#login").animate({ top: "-10" }, 30);
      $("#login").animate({ top: "0" }, 30);
      $("#login").animate({ top: "-10" }, 30);
      $("#login").animate({ top: "0" }, 30);
    });
  });

  return (
    <div
      className="Login"
      style={{ backgroundColor: "#D291BC", height: "100vh" }}
    >
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />
      <br />

      <Container id="loginout">
        <Row>
          <center>
            <Col xs={12} md={5}>
              <Card
                id="login"
                style={{
                  zIndex: "9999",
                  width: "100%",
                  height: "",
                  backgroundColor: "#4346aa",
                  color: "rgb(206, 206, 206)",
                  boxShadow: "white 0px 0px 5px 0px",
                  borderRadius: "10px",
                }}
              >
                <Card.Header
                  style={{
                    background: "#2c2f8b",
                    borderRadius: "10px 10px 0px 0px",
                  }}
                >
                  <strong>Login board</strong>
                </Card.Header>
                <Card.Body>
                  <Container fluid>
                    <Row style={{ margin: "10px" }}>
                      <Col xs={12} md={4}>
                        <strong style={{ float: "left", fontSize: "small" }}>
                          <label>User Name:</label>
                        </strong>
                      </Col>
                      <Col xs={12} md={8}>
                        <input
                          id="login-name"
                          style={{
                            float: "left",
                            border: "none",
                            borderBottom: "blue solid 1px",
                            backgroundColor: "rgb(206, 206, 206)",
                            width: "100%",
                          }}
                          type="text"
                        />
                      </Col>
                    </Row>

                    <Row style={{ margin: "10px" }}>
                      <Col xs={12} md={4}>
                        <strong style={{ float: "left", fontSize: "small" }}>
                          <label>Password:</label>
                        </strong>
                      </Col>
                      <Col xs={12} md={8}>
                        <input
                          id="login-pass"
                          style={{
                            float: "left",
                            border: "none",
                            borderBottom: "blue solid 1px",
                            backgroundColor: "rgb(206, 206, 206)",
                            width: "100%",
                          }}
                          type="password"
                        />
                      </Col>
                    </Row>

                    <Row style={{ margin: "10px" }}>
                      <Col md={4}></Col>
                      <Col xs={12} md={8}>
                        <Button
                          onClick={loginAdmin}
                          style={{
                            float: "left",
                            width: "100%",
                            backgroundColor: "#2c2f8b",
                          }}
                          variant="primary"
                        >
                          Go
                        </Button>
                      </Col>
                    </Row>
                  </Container>
                </Card.Body>
              </Card>
            </Col>
          </center>
        </Row>
      </Container>
    </div>
  );
}

export default Login;
