import { Table, Row, Col, Container, Button } from "react-bootstrap";
import "./Accountant.css";
import { useContext } from "react";
import { DataContext } from "../../Data/DataContext";
import $ from "jquery";
function Tr(props) {
  const [
    admin,
    setAdmin,
    nurse,
    setNurse,
    accountant,
    setAccountant,
    audit,
    setAudit,
    report,
    setReport,
    admins,
    setAdmins,
    useredit,
    setUseredit,
    userModal,
    setUserModal,
  ] = useContext(DataContext);

  // Ths function to display the user data that want to edit
  const editRow = (id, name) => {
    setUserModal(true);
    if (name == "user") {
      const u = user.data.filter((d) => d.id == id);
      setUseredit(u[0]);
    } else {
      const u = clinic.data.filter((d) => d.id == id);
      setUseredit(u[0]);
    }
  };

  // Thus function to delete  user
  const deleteRow = (id) => {
    $.ajax({
      url: "http://touch.extra-laboratory.com/api/delete_user.php",
      type: "post",
      data: {
        id: id,
      },
      success: function (data) {
        let u = JSON.parse(data);
        if (u.code == 200) {
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_users.php",
            success: function (data) {
              let d = JSON.parse(data);
              setUser(d);
            },
            error: function (error) {
              console.log(error);
            },
          });
          $.ajax({
            url: "http://touch.extra-laboratory.com/api/get_clinics.php",
            success: function (data) {
              let d = JSON.parse(data);
              setClinic(d);
            },
            error: function (error) {
              console.log(error);
            },
          });
          setUserModal(false);
        } else alert(u.message);
      },
      error: function (error) {
        console.log(error);
      },
    });
  };

  return (
    <tr>
      <th>#</th>
      <th>{props.name}</th>
      <th>{props.phone}</th>
      <th>{props.email}</th>
      <th>{props.city}</th>
      <th>{props.account}</th>
      <th>{props.date}</th>
      <th>
        <Button
          variant="outline-primary"
          size="sm"
          onClick={() => editRow(props.delete, props.type)}
        >
          Edit
        </Button>
      </th>
      <th>
        <Button
          onClick={() => deleteRow(props.delete)}
          variant="outline-primary"
          size="sm"
        >
          Delete
        </Button>
      </th>
    </tr>
  );
}

export default Tr;
